<span class="line"><span>        success: function (resp) {//请求成功返回后的回掉函数</span></span>
<span class="line"><span>            console.log(resp);  //打印返回的数据</span></span>
<span class="line"><span>        }</span></span>
<span class="line"><span>    });</span></span>
<span class="line"><span>&#x3C;/script></span></span></code></pre>
